mapper.update:
update eden_board
SET board_title   = #{board_title},
    board_content = #{board_content}
where board_no = #{board_no}

mapper.select:
select count(*)
from eden_board_like
where user_no = #{user_no}
  and board_no = #{board_no}

mapper.delete:
delete
from eden_board_like
where board_no = #{board_no}
  and user_no = #{user_no}

mapper.select:
select count(*)
from eden_board_like
where board_no = #{board_no}
