mapper.select:
select count(*)
from eden_comment
where board_no = #{board_no}

mapper.update:
update eden_comment
set comment_content = #{comment_content}
where comment_no = #{comment_no}
  and user_no = #{user_no}

mapper.select:
select count(*)
from eden_comment_like
where comment_no = #{comment_no}
  and user_no = #{user_no}

mapper.delete:
delete
from eden_comment_like
where comment_no = #{comment_no}
  and user_no = #{user_no}
